mapper.select:
SELECT `name`, grade, college, major, class, dsp, dno,secdno, gender, sno, qq, domitory, know, party
FROM db_sign_up.t_sign
WHERE gender = #{gender}

SELECT `name`, grade, college, major, class, dsp, dno,secdno, gender, sno, qq, domitory, know, party
FROM db_sign_up.t_sign
WHERE college = #{college}

SELECT `name`, grade, college, major, class, dsp, dno,secdno, gender, sno, qq, domitory, know, party
FROM db_sign_up.t_sign
WHERE dno = #{dno}

SELECT count(uid)
FROM db_sign_up.t_sign
WHERE uid = #{uid}
